import React, { Component } from 'react';
import './App.css';
import 'semantic-ui-css/semantic.min.css';

import Login from './components/pages/Login';
import Home from './components/pages/Home';
import fire from './configs/Fire';

import {userUpdate} from './actions/userAction';
import {Route} from 'react-router-dom';
import Auth from './helpers/auth';
import {connect} from 'react-redux';

class App extends Component {
  state={
    user:null,
    allow:false
  }
  constructor(props)
  {
    super(props);
    this.authListener=this.authListener.bind(this);
  }
  componentDidMount() {
    this.authListener();
    this.authControl();
  }
  
  authListener()
  {
    fire.auth().onAuthStateChanged((user)=>
    {     
      console.log("auth listener çalışsın");
      this.setState({user});
      localStorage.setItem('user',JSON.stringify(user));
      
    })
  }
  authControl=()=>
  {
    if(Object.keys(this.props.user).length)
    {
      this.setState({
        allow:true
      })
    }else{
      
      let userLocal=localStorage.getItem("user");
      if(userLocal)
      {
        let userParse=JSON.parse(userLocal);
        if(userParse) this.props.userUpdate(userParse);
      }
    }    
  }

  content=(<Route exact path="/" >
    <Home/>
  </Route>
  )

  render() {
    return (
      <div className="App" loading="true">
        
        {this.state.allow?this.content:(<Login />)}
      </div>
    );
  }
}
const mapPropsToState=({user})=>{
  return {user}
};
const mapDispatchToState={
  userUpdate
};

export default connect(mapPropsToState,mapDispatchToState)(App);
