import React, { Component } from 'react';
import './App.css';
import 'semantic-ui-css/semantic.min.css';
import { Dimmer, Loader, Image, Segment } from 'semantic-ui-react'
import Login from './components/pages/Login';
import Home from './components/pages/Home';
import fire from './configs/Fire';
import {Route} from 'react-router-dom';
import Auth from './helpers/auth';
import {connect} from 'react-redux';

class App extends Component {
  state={
    user:null,
    allow:false
  }
  constructor(props)
  {
    super(props);
    this.authListener=this.authListener.bind(this);
  }
  componentDidMount() {
    this.authListener();
  }
  
  authListener()
  {
    fire.auth().onAuthStateChanged((user)=>
    {     
      console.log("auth listener çalışsın");
      this.setState({user});
      localStorage.setItem('user',JSON.stringify(user));
      
    })
  }
  authControl=()=>
  {
    if(this.props.users.user)
    {
      this.setState({
        allow:true
      })
    }else{
      
      const userLocal=localStorage.getItem("user");

    }    
  }

  content=(<Route exact path="/" >
    anasayfa
  </Route>
  )

  render() {
    return (
      <div className="App" loading="true">
        <Segment>
          <Dimmer active>
            <Loader />
          </Dimmer>

          <Image src='https://react.semantic-ui.com/images/wireframe/short-paragraph.png' />
        </Segment>
        {this.state.allow?this.content:(<Login />)}
      </div>
    );
  }
}
const mapPropsToState=({users})=>{
  return {users}
};
const mapDispatchToState={

};

export default connect(mapPropsToState,mapDispatchToState)(App);
